INSERT INTO `crm_notification` (`id`, `event`, `name`, `transport`, `status`, `subject`, `body`, `recipient`, `sender`, `company_id`) VALUES
(1, 'invoice.issue', 'Invoice issued', 'email', 1, 'New invoice No. {$invoice.number} issued for you by {$company.name}', '<p>Здравствуйте,</p>\n<p>Вам выставлен счёт №{$invoice.number|escape} от {$company.name|escape}.</p>\n\n<p>Номер счёта: {$invoice.number|escape}<br>\nСумма: {wa_currency($invoice.amount, $invoice.currency_id)}<br>\nДата счёта: {$invoice.invoice_date|wa_date}</p>\n\n<p>Состав счёта:</p>\n<ol>\n{foreach $invoice.items as $item}\n<li>{$item.name|escape}: {$item.quantity|wa_format_number:false} шт.</li>\n{/foreach}\n</ol>\n\n<p>\nСсылка для онлайн-просмотра счёта:<br>\n<a href=\"{$link}\">{$link}</a>\n</p>\n\n<p>\n–-<br>\n{$wa->accountName()|escape}<br>\n{$wa->domainUrl()}\n</p>', 'client', 'system', NULL),
(2, 'invoice.payment', 'Invoice payment', 'email', 1, 'Received payment for invoice No. {$invoice.number} from {$company.name}', '<p>Здравствуйте,</p>\n<p>Мы получили ваш платеж по счёту №{$invoice.number|escape} от {$company.name|escape}. Спасибо!</p>\n\n<p>Номер счёта: {$invoice.number|escape}<br>\nСумма: {wa_currency($invoice.amount, $invoice.currency_id)}<br>\nДата счёта: {$invoice.invoice_date|wa_date}</p>\n\n<p>Состав счёта:</p>\n<ol>\n{foreach $invoice.items as $item}\n<li>{$item.name|escape}: {$item.quantity|wa_format_number:false} шт.</li>\n{/foreach}\n</ol>\n\n<p>\nСсылка для онлайн-просмотра счёта:<br>\n<a href=\"{$link}\">{$link}</a>\n</p>\n\n<p>\n–-<br>\n{$wa->accountName()|escape}<br>\n{$wa->domainUrl()}\n</p>', 'client', 'system', NULL),
(3, 'invoice.cancel', 'Invoice canceled', 'email', 1, 'Canceled invoice No. {$invoice.number} from {$company.name}', '<p>Здравствуйте,</p>\n<p>Отменён счёт №{$invoice.number|escape} от {$company.name|escape}.</p>\n\n<p>Номер счёта: {$invoice.number|escape}<br>\nСумма: {wa_currency($invoice.amount, $invoice.currency_id)}<br>\nДата счёта: {$invoice.invoice_date|wa_date}</p>\n\n<p>Состав счёта:</p>\n<ol>\n{foreach $invoice.items as $item}\n<li>{$item.name|escape}: {$item.quantity|wa_format_number:false} шт.</li>\n{/foreach}\n</ol>\n\n<p>\n–-<br>\n{$wa->accountName()|escape}<br>\n{$wa->domainUrl()}\n</p>', 'client', 'system', NULL),
(4, 'invoice.expire', 'Invoice expired', 'email', 1, 'Invoice {$invoice.number} has expired', '<p>Здравствуйте,</p>\n<p>Истек срок действия счёта №{$invoice.number|escape} от {$company.name|escape}.</p>\n\n<p>Номер счёта: {$invoice.number|escape}<br>\nСумма: {wa_currency($invoice.amount, $invoice.currency_id)}<br>\nДата счёта: {$invoice.invoice_date|wa_date}</p>\n\n<p>Состав счёта:</p>\n<ol>\n{foreach $invoice.items as $item}\n<li>{$item.name|escape}: {$item.quantity|wa_format_number:false} шт.</li>\n{/foreach}\n</ol>\n\n<p>\n–-<br>\n{$wa->accountName()|escape}<br>\n{$wa->domainUrl()}\n</p>\n', 'client', 'system', NULL),
(5, 'deal.stage_overdue', 'Stage time limit expired for deal', 'email', 1, 'responsible', '<p>\n    {waDateTime::format(\'datetime\')} истекло время, отведённое для нахождения сделки «<a href=\"{$deal.url}\">{$deal.name|escape}</a>»\n    на этапе «{$deal.stage.name|escape}» воронки «{$deal.funnel.name|escape}»\n    ({_w(\'%d hour\', \'%d hours\', $deal.limit_hours)}).\n</p>\n<p>\n    <b>Информация о сделке</b><br>\n    Клиент: {$customer.name|escape}{if !empty($customer.company)}, {$customer.company|escape}{/if}<br>\n    Описание: {$deal.description_sanitized}<br>\n    Предварительная стоимость: {$deal.amount|wa_format_amount_currency:$deal.currency_id}<br>\n    Предварительная дата закрытия: {$deal.expected_date|wa_date}<br>\n</p>\n\n--<br>\n<p>\n    --<br>\n    {$wa->accountName()|escape}<br>\n    {$wa->domainUrl()}\n</p>\n', 'Stage time limit expired for deal', 'system', NULL),
(6, 'deal.create', 'Deal created', 'email', 1, 'responsible', '<p>\n    {$deal.create_datetime|wa_datetime} создана новая сделка\n    «<a href=\"{$deal.url}\">{$deal.name|escape}</a>»\n    на этапе «{$deal.stage.name|escape}» воронки «{$deal.funnel.name|escape}».\n</p>\n<p>\n    <b>Информация о сделке</b><br>\n    Клиент: {$customer.name|escape}{if !empty($customer.company)}, {$customer.company|escape}{/if}<br>\n    Описание: {$deal.description_sanitized}<br>\n    Предварительная стоимость: {$deal.amount|wa_format_amount_currency:$deal.currency_id}<br>\n    Предварительная дата закрытия: {$deal.expected_date|wa_date}<br>\n</p>\n\n--<br>\n<p>\n    --<br>\n    {$wa->accountName()|escape}<br>\n    {$wa->domainUrl()}\n</p>\n', 'Deal created', 'system', NULL),
(7, 'deal.move', 'Deal moved to another stage', 'email', 1, 'responsible', '<p>\n    {$deal.create_datetime|wa_datetime} сделка\n    «<a href=\"{$deal.url}\">{$deal.name|escape}</a>»\n    была перемещена с этапа «{$deal.before_stage.name|escape}» воронки «{$deal.before_funnel.name|escape}»\n    на этап «{$deal.stage.name|escape}» воронки «{$deal.funnel.name|escape}».\n</p>\n<p>\n    <b>Информация о сделке</b><br>\n    Клиент: {$customer.name|escape}{if !empty($customer.company)}, {$customer.company|escape}{/if}<br>\n    Описание: {$deal.description_sanitized}<br>\n    Предварительная стоимость: {$deal.amount|wa_format_amount_currency:$deal.currency_id}<br>\n    Предварительная дата закрытия: {$deal.expected_date|wa_date}<br>\n    Ответственный: {$responsible.name|escape}\n</p>\n\n--<br>\n<p>\n    --<br>\n    {$wa->accountName()|escape}<br>\n    {$wa->domainUrl()}\n</p>\n', 'Deal moved to another stage', 'system', NULL),
(8, 'deal.won', 'Deal won', 'email', 1, 'responsible', '<p>\n    {waDateTime::format(\'datetime\')} выиграна сделка\n    «<a href=\"{$deal.url}\">{$deal.name|escape}</a>»\n    в воронке «{$deal.funnel.name|escape}».\n    Ответственный: {$responsible.name|escape}.\n</p>\n<p>\n    <b>Информация о сделке</b><br>\n    Клиент: {$customer.name|escape}{if !empty($customer.company)}, {$customer.company|escape}{/if}<br>\n    Описание: {$deal.description_sanitized}<br>\n    Клиентом оплачены счета на сумму: {$deal.amount|wa_format_amount_currency:$deal.currency_id}\n</p>\n\n--<br>\n<p>\n    --<br>\n    {$wa->accountName()|escape}<br>\n    {$wa->domainUrl()}\n</p>\n', 'Deal won', 'system', NULL),
(9, 'deal.lost', 'Deal lost', 'email', 1, 'responsible', '<p>\n    {waDateTime::format(\'datetime\')} потеряна сделка\n    «<a href=\"{$deal.url}\">{$deal.name|escape}</a>»\n    в воронке «{$deal.funnel.name|escape}».\n    Ответственный: {$responsible.name|escape}.\n</p>\n<p>\n    <b>Информация о сделке</b><br>\n    Клиент: {$customer.name|escape}{if !empty($customer.company)}, {$customer.company|escape}{/if}<br>\n    Описание: {$deal.description_sanitized}<br>\n    Предварительная стоимость: {$deal.amount|wa_format_amount_currency:$deal.currency_id}\n    Предварительная дата закрытия: {$deal.expected_date|wa_datetime}\n</p>\n\n--<br>\n<p>\n    --<br>\n    {$wa->accountName()|escape}<br>\n    {$wa->domainUrl()}\n</p>\n', 'Deal lost', 'system', NULL),
(10, 'deal.restore', 'Deal reopened', 'email', 1, 'responsible', '<p>\n    {waDateTime::format(\'datetime\')} переоткрыта\n    {if $deal.status_id == crmDealModel::STATUS_WON}выигранная{else}проигранная{/if} сделка\n    «<a href=\"{$deal.url}\">{$deal.name|escape}</a>»\n    на этапе «{$deal.stage.name|escape}» в воронке «{$deal.funnel.name|escape}».\n</p>\n<p>\n    <b>Информация о сделке</b><br>\n    Клиент: {$customer.name|escape}{if !empty($customer.company)}, {$customer.company|escape}{/if}<br>\n    Описание: {$deal.description_sanitized}<br>\n    Предварительная стоимость: {$deal.amount|wa_format_amount_currency:$deal.currency_id}\n    Предварительная дата закрытия: {$deal.expected_date|wa_datetime}\n    Ответственный: {$responsible.name|escape}.\n</p>\n\n--<br>\n<p>\n    --<br>\n    {$wa->accountName()|escape}<br>\n    {$wa->domainUrl()}\n</p>\n', 'Deal reopened', 'system', NULL);
INSERT INTO `crm_template` (`id`, `name`, `content`, `origin_id`, `style_version`) VALUES
(1, 'Variant A', '{$_logo = \"{$wa_app_static_url}img/crm.svg?v={$wa->version()}\"}\n{if !empty($company.logo_url)}\n    {$_logo = $company.logo_url}\n{/if}\n\n{if !empty($company.invoice_options) && !empty($company.invoice_options.headers_color)}\n    {$_color = $company.invoice_options.headers_color}\n{/if}\n\n<div class=\"c-front-invoice-block type-1\">\n    <div class=\"c-header c-text-center\">\n        <div class=\"c-logo\">\n            <img src=\"{$_logo}\" alt=\"\">\n        </div>\n\n        <p class=\"c-date c-color-gray\">[`Invoice`] [`#`]{$invoice.number|escape} [`issued on`] {$invoice.invoice_date|wa_date}</p>\n\n        <p class=\"c-price\"{if !empty($_color)} style=\"color: {$_color|escape};\"{/if}>{$invoice.amount|wa_format_amount_currency:$invoice.currency_id}</p>\n\n        {$pay_button}\n\n        <div class=\"c-details\">\n            <div class=\"c-recipient\">\n                <p class=\"c-font-bold fs-16\"{if !empty($_color)} style=\"color: {$_color|escape};\"{/if}>[`Provider`]</p>\n                <p>\n                    {ifempty($company.name, $invoice.company_id)|escape}{if !empty($company.address)},\n                        {$company.address|escape|nl2br}{/if}{if !empty($company.phone)},\n                        {$company.phone|escape}{/if}\n                </p>\n            </div>\n\n            <div class=\"c-payer\">\n                <p class=\"c-font-bold fs-16\"{if !empty($_color)} style=\"color: {$_color|escape};\"{/if}>[`Billed To`]</p>\n                <p>\n                    {$_city = $customer->get(\'address:city\', \'default\')}\n                    {if $customer.company}{$customer.company|escape}{else}{$customer.name|escape}{/if}{if $_city},\n                        {$_city|escape}\n                    {/if}\n                </p>\n            </div>\n        </div>\n    </div>\n    <div class=\"c-items\">\n        <table>\n            <thead>\n            <tr>\n                <th class=\"c-text-left c-h-mobile c-font-bold c-pr-10 fs-16\"{if !empty($_color)} style=\"color: {$_color|escape};\"{/if}>№</th>\n                <th class=\"c-text-left c-text-nowrap c-font-bold c-pr-30 fs-16\"{if !empty($_color)} style=\"color: {$_color|escape};\"{/if}>[`Product or service`]</th>\n                <th class=\"c-h-mobile c-text-nowrap c-font-bold c-text-right c-pr-30 fs-16\"{if !empty($_color)} style=\"color: {$_color|escape};\"{/if}>[`Qty`]</th>\n                <th class=\"c-h-mobile c-font-bold c-text-right c-pr-30 fs-16\"{if !empty($_color)} style=\"color: {$_color|escape};\"{/if}>[`Price`]</th>\n                <th class=\"c-text-nowrap c-font-bold c-text-right fs-16\"{if !empty($_color)} style=\"color: {$_color|escape};\"{/if}>[`Amount`] ({$invoice.currency_id})</th>\n            </tr>\n            </thead>\n            <tbody>\n            {$_subtotal = 0}\n            {foreach $invoice.items as $_item}\n            <tr>\n                {$_item_total = ($_item.quantity * $_item.price)}\n                <td class=\"c-h-mobile c-align-top c-pr-10 c-pt-12\">{$_item@iteration}</td>\n                <td class=\"c-pr-30 c-pt-12\">\n                    <span data-order=\"{$_item@iteration}.\">{$_item.name|escape}</span>\n                    <span class=\"c-d-mobile c-color-gray\">{$_item.price|wa_format} x {$_item.quantity|wa_format}</span>\n                </td>\n                <td class=\"c-h-mobile c-text-nowrap c-align-top c-text-right c-pr-30 c-pt-12\">{$_item.quantity|wa_format}</td>\n                <td class=\"c-h-mobile c-text-nowrap c-align-top c-text-right c-pr-30 c-pt-12\">{$_item.price|wa_format}</td>\n                <td class=\"c-text-nowrap c-align-top c-text-right c-pt-12\">{$_item_total|wa_format}</td>\n                {$_subtotal = $_subtotal + $_item_total}\n            </tr>\n            {/foreach}\n            </tbody>\n        </table>\n    </div>\n    <hr>\n    <div class=\"c-total\">\n        <table>\n            <tr>\n                <td class=\"c-color-gray c-pr-30 fs-16\">[`Subtotal`]</td>\n                <td>{$_subtotal|wa_format}</td>\n            </tr>\n            {if ($invoice.tax_type != \"NONE\")}\n                {$_type = \"\"}\n                {if ($invoice.tax_type == \"INCLUDE\")}\n                    {$_type = \"[`including`]\"}\n                {/if}\n\n                {$_name = \"[`tax`]\"}\n                {if !empty($invoice.tax_name)}\n                    {$_name = $invoice.tax_name}\n                {/if}\n                <tr>\n                    <td class=\"c-color-gray c-pr-30 c-pt-12 fs-16\">\n                        {if !empty($_type)}{$_type}&nbsp;{/if}{$_name|escape}&nbsp;{$invoice.tax_percent|escape}%\n                    </td>\n                    <td class=\"c-pt-12\">\n                        {$invoice.tax_amount|wa_format}\n                    </td>\n                </tr>\n            {/if}\n            <tr>\n                <td class=\"c-color-gray c-pr-30 c-pt-12 fs-16\">[`Total`]</td>\n                <td class=\"c-font-bold c-pt-12\"{if !empty($_color)} style=\"color: {$_color|escape};\"{/if}>{$invoice.amount|wa_format_amount_currency:$invoice.currency_id}</td>\n            </tr>\n        </table>\n    </div>\n    {if !empty($invoice.comment)}\n        <div class=\"c-comment\">\n            <p class=\"c-font-bold fs-16\"{if !empty($_color)} style=\"color: {$_color|escape};\"{/if}>[`Comment`]</p>\n            <p>{$invoice.comment}</p>\n        </div>\n    {/if}\n</div>\n', 1, 2),
(2, 'Variant B', '{$_logo = \"{$wa_app_static_url}img/crm.svg?v={$wa->version()}\"}\n{if !empty($company.logo_url)}\n    {$_logo = $company.logo_url}\n{/if}\n\n<div class=\"c-front-invoice-block type-2\">\n    <div class=\"c-header\">\n        <div class=\"c-logo\">\n            <img src=\"{$_logo}\" alt=\"\">\n        </div>\n\n        <div class=\"c-header-wrapper\">\n            <div class=\"c-info\">\n                <p class=\"c-color-gray c-my-0 c-mb-8 fs-16\">[`Invoice`]</p>\n                <p class=\"c-date c-my-0\">[`#`]{$invoice.number|escape} [`issued on`] {$invoice.invoice_date|wa_date}</p>\n\n                <p class=\"c-price\">{$invoice.amount|wa_format_amount_currency:$invoice.currency_id}</p>\n\n                {$pay_button}\n            </div>\n\n            <div class=\"c-details\">\n                <div class=\"c-recipient\">\n                    <p class=\"c-color-gray fs-16\">[`Provider`]</p>\n                    <p>\n                        {ifempty($company.name, $invoice.company_id)|escape}{if !empty($company.address)},\n                            {$company.address|escape|nl2br}{/if}{if !empty($company.phone)},\n                            {$company.phone|escape}{/if}\n                    </p>\n                </div>\n\n                <div class=\"c-payer\">\n                    <p class=\"c-color-gray fs-16\">[`Billed To`]</p>\n                    <p>\n                        {$_city = $customer->get(\'address:city\', \'default\')}\n                        {if $customer.company}{$customer.company|escape}{else}{$customer.name|escape}{/if}{if $_city},\n                            {$_city|escape}\n                        {/if}\n                    </p>\n                </div>\n                {if !empty($invoice.comment)}\n                <div class=\"c-comment\">\n                    <p class=\"c-color-gray fs-16\">[`Comment`]</p>\n                    <p>{$invoice.comment}</p>\n                </div>\n                {/if}\n            </div>\n        </div>\n\n    </div>\n    <div class=\"c-items\">\n        <table>\n            <thead>\n            <tr>\n                <th class=\"c-text-left c-h-mobile c-color-gray c-pr-10 fs-16\">№</th>\n                <th class=\"c-text-left c-text-nowrap c-color-gray c-pr-30 fs-16\">[`Product or service`]</th>\n                <th class=\"c-h-mobile c-text-nowrap c-color-gray c-text-right c-pr-30 fs-16\">[`Qty`]</th>\n                <th class=\"c-h-mobile c-color-gray c-text-right c-pr-30 fs-16\">[`Price`]</th>\n                <th class=\"c-text-nowrap c-color-gray c-text-right fs-16\">[`Amount`] ({$invoice.currency_id})</th>\n            </tr>\n            </thead>\n            <tbody>\n            {$_subtotal = 0}\n            {foreach $invoice.items as $_item}\n            <tr>\n                {$_item_total = ($_item.quantity * $_item.price)}\n                <td class=\"c-h-mobile c-align-top c-pr-10 c-pt-12\">{$_item@iteration}</td>\n                <td class=\"c-pr-30 c-pt-12\">\n                    <span data-order=\"{$_item@iteration}.\">{$_item.name|escape}</span>\n                    <span class=\"c-d-mobile c-color-gray\">{$_item.price|wa_format} x {$_item.quantity|wa_format}</span>\n                </td>\n                <td class=\"c-h-mobile c-text-nowrap c-align-top c-text-right c-pr-30 c-pt-12\">{$_item@iteration}</td>\n                <td class=\"c-h-mobile c-text-nowrap c-align-top c-text-right c-pr-30 c-pt-12\">{$_item.price|wa_format}</td>\n                <td class=\"c-text-nowrap c-align-top c-text-right c-pt-12\">{$_item_total|wa_format}</td>\n                {$_subtotal = $_subtotal + $_item_total}\n            </tr>\n            {/foreach}\n            </tbody>\n        </table>\n    </div>\n    <hr>\n    <div class=\"c-total\">\n        <table>\n            <tr>\n                <td class=\"c-color-gray c-pr-30 fs-16\">[`Subtotal`]</td>\n                <td>{$_subtotal|wa_format}</td>\n            </tr>\n            {if ($invoice.